INSERT INTO `hls2_street_history` (`id`, `cid`, `street_id`, `title`, `title_cn`, `title_en`, `description`, `description_cn`, `description_en`, `content`, `content_cn`, `content_en`, `cover`, `cover_width_300`, `images`, `status`, `recommend`, `re_time`, `view`, `sort`, `created`, `updated`, `deleted`) VALUES
(1, 1, 1, '“荷蘭園”典故', NULL, NULL, '肥利喇亞美打大馬路為葡文譯音，是根據葡人阿爾梅達（Almeida）（1895年時任命為葡海事暨海外部長）而命名。由於名稱很不順口，不易記憶；另外此地又是荷蘭戰俘安置地，便有“荷蘭園”之稱。', NULL, NULL, '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201105/1604586592260686.png&quot; title=&quot;&quot; alt=&quot;荷兰园大马路地图_看图王.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;1622年，荷蘭人駕艦進襲澳門，荷軍繞道松山，為葡軍包圍，成為了俘虜，由澳葡當局安置在遠離古澳城的一處蔡姓地主的廢棄莊園裡，為澳葡修城牆築炮臺。最初在該地居住的是華人，還有一些非葡籍的外國教士。到了清同治年間，澳葡拆除水坑尾城牆，開闢該地，將其規劃為一分為二的街區，並以兩條大街橫劃之，即肥利喇亞美打大馬路（今荷蘭園大馬路）和亞卑寮奴你士街（今荷蘭園二馬路）。肥利喇亞美打大馬路為葡文譯音，是根據葡人阿爾梅達（Almeida）（1895年時任命為葡海事暨海外部長）而命名。由於名稱很不順口，不易記憶；另外此地又是荷蘭戰俘安置地，便有“荷蘭園”之稱。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em; text-indent: 0em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 0em; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 16px;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201102/1604307123785959.jpg&quot; title=&quot;&quot; alt=&quot;荷兰园典故插图.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;荷蘭園大馬路故事&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;荷蘭園大馬路正名叫“肥利喇亞美打”，是根據葡人阿爾梅達（葡文）而命名。阿爾梅達出生於1847年，卒於1902年，且在1895年任葡海事暨海外部長。澳門市民一般稱之為荷蘭園大馬路或荷蘭園正街，簡稱荷蘭園。荷蘭園大馬路是一條歷史悠久的街道，除了是為澳門交通要道之一，過往更是本澳主要商業購物區。荷蘭園大馬路是連接塔石廣場和新馬路水坑尾路最中心的街道，背面又是享負盛名的望得堂區創意園，聚集了多個頗具特色的景點。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em; text-indent: 0em; text-align: center;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201102/1604307134546490.jpg&quot; title=&quot;&quot; alt=&quot;荷蘭園典故插圖(1).jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2020-10-31/5f9cbf3d41467.jpg', '/Public/upload/2020-10-31/5f9cbf3d41467_width_300.jpg', NULL, 'published', 1, 1604586594, NULL, 6, 1540455305, 1604586594, NULL),
(2, 1, 2, '二馬路故事', NULL, NULL, '​  荷蘭園二馬路，又稱亞卑寮奴你士街（Rua do Abreu Nunes），是澳門望德堂區南部的街道，位於高偉樂街和東望洋街之間，與荷蘭園大馬路和東望洋街平行。呈東北至西南走向。為南北單線行車。本街有不少泰國人經營的商店，故有“小泰國”之稱。又稱荷蘭園二馬路為“泰國街”。', NULL, NULL, '&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;荷蘭園二馬路，又稱亞卑寮奴你士街（Rua do Abreu&amp;nbsp;Nunes），是澳門望德堂區南部的街道，位於高偉樂街和東望洋街之間，與荷蘭園大馬路和東望洋街平行。呈東北至西南走向。為南北單線行車。本街有不少泰國人經營的商店，故有“小泰國”之稱。又稱荷蘭園二馬路為“泰國街”。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181025/1540455474520176.jpg&quot; title=&quot;1540455474520176.jpg&quot; alt=&quot;DSC_0050_副本.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-25/5bd17c5586248.JPG', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540455517, 1540455517, 1540530522),
(3, 1, 3, '望德堂故事', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 14px;&quot;&gt;座落在澳門著名街道荷蘭園大馬路背後的望德堂區，是澳門中區一處恍如南歐小鎮的舊城區，因第一座華人教堂望德聖母堂巍然矗立而得名。&lt;/span&gt;&lt;span style=&quot;font-family: 宋体; font-size: 14px;&quot;&gt;1569&lt;/span&gt;&lt;span style=&quot;font-family: 宋体; font-size: 14px;&quot;&gt;年，創立仁慈堂的賈尼路（&lt;/span&gt;&lt;span style=&quot;font-size: 14px; font-family: Calibri;&quot;&gt;D. Belchior Carneiro&lt;/span&gt;&lt;span style=&quot;font-family: 宋体; font-size: 14px;&quot;&gt;）主教，在望德堂區開設拉匝祿（&lt;/span&gt;&lt;span style=&quot;font-size: 14px; font-family: Calibri;&quot;&gt;Lázaro&lt;/span&gt;&lt;span style=&quot;font-family: 宋体; font-size: 14px;&quot;&gt;）痲瘋病院，望德堂因此被稱為“瘋堂”，附近一帶形成的街道也因而“瘋”起來，有瘋堂斜巷、瘋堂新街、瘋堂裡、瘋堂圍、瘋堂中斜巷等。&lt;/span&gt;&lt;span style=&quot;font-size: 14px; font-family: Calibri;&quot;&gt;1984&lt;/span&gt;&lt;span style=&quot;font-family: 宋体; font-size: 14px;&quot;&gt;年望德堂坊區被列入政府保護文化財產之一。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181025/1540455634499249.jpg&quot; title=&quot;1540455634499249.jpg&quot; alt=&quot;1540455634499249.jpg&quot; width=&quot;400&quot; height=&quot;308&quot; style=&quot;width: 400px; height: 308px;&quot; border=&quot;0&quot; vspace=&quot;0&quot;/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-25/5bd17d4e0e641.jpg', '/Public/upload/2018-10-25/5bd17d4e0e641_width_300.jpg', NULL, 'published', 0, NULL, NULL, 0, 1540455769, 1540456324, 1604108168),
(4, 1, 1, '“荷蘭園”典故', NULL, NULL, '1622年，荷蘭人駕艦進襲澳門，荷軍繞道松山，為葡軍包圍，成為了俘虜，由澳葡當局安置在遠離古澳城的一處蔡姓地主的廢棄莊園裡，為澳葡修城牆築炮臺。', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;1622年，荷蘭人駕艦進襲澳門，荷軍繞道松山，為葡軍包圍，成為了俘虜，由澳葡當局安置在遠離古澳城的一處蔡姓地主的廢棄莊園裡，為澳葡修城牆築炮臺。最初在該地居住的是華人，還有一些非葡籍的外國教士。到了清同治年間，澳葡拆除水坑尾城牆，開闢該地，將其規劃為一分為二的街區，並以兩條大街橫劃之，即肥利喇亞美打大馬路（今荷蘭園大馬路）和亞卑寮奴你士街（今荷蘭園二馬路）。肥利喇亞美打大馬路為葡文譯音，是根據葡人阿爾梅達（&lt;span style=&quot;font-family: Calibri;&quot;&gt;Almeida&lt;/span&gt;）（&lt;span style=&quot;font-family: Calibri;&quot;&gt;1895&lt;/span&gt;年時任命為葡海事暨海外部長）而命名。由於名稱很不順口，不易記憶；另外此地又是荷蘭戰俘安置地，便有“荷蘭園”之稱。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540519028296930.jpg&quot; title=&quot;1540519028296930.jpg&quot; alt=&quot;荷兰园故事.jpg&quot;/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd274814574c.jpg', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540519066, 1540519066, 1540531054),
(5, 1, 1, '荷蘭園大馬路故事', NULL, NULL, '荷蘭園大馬路正名叫“肥利喇亞美打”，是根據葡人阿爾梅達（葡文）而命名。阿爾梅達出生於1847年，卒於1902年，且在1895年任葡海事暨海外部長', NULL, NULL, '&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;荷蘭園大馬路正名叫&lt;/span&gt;“肥利喇亞美打”，是根據葡人阿爾梅達（葡文）而命名。阿爾梅達出生於&lt;/span&gt;&lt;span style=&quot;font-family: Calibri;&quot;&gt;1847&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;年，卒於&lt;/span&gt;&lt;span style=&quot;font-family: Calibri;&quot;&gt;1902&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;年，且在&lt;/span&gt;&lt;span style=&quot;font-family: Calibri;&quot;&gt;1895&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;年任葡海事暨海外部長。澳門市民一般稱之為荷蘭園大馬路或荷蘭園正街，簡稱荷蘭園。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: center;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540519366400532.jpg&quot; title=&quot;1540519366400532.jpg&quot; alt=&quot;104_0466_看图王.jpg&quot;/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;荷蘭園大馬路是一條歷史悠久的街道，除了是為澳門交通要道之一，過往更是本澳主要商業購物區。荷蘭園大馬路是連接塔石廣場和新馬路水坑尾路最中心的街道，背面又是享負盛名的望得堂區創意園，聚集了多個頗具特色的景點。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540519179115459.jpg&quot; title=&quot;1540519179115459.jpg&quot; alt=&quot;IMG_0971荷蘭園大馬路_看图王.jpg&quot;/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd275a8d988b.jpg', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540519390, 1540519390, 1540530572),
(6, 1, 2, '二馬路故事', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: center; line-height: 3em; text-indent: 0em;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201105/1604586041567939.png&quot; title=&quot;&quot; alt=&quot;荷兰园二马路地图-01.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;荷蘭園二馬路，又稱亞卑寮奴你士街（Rua do Abreu&amp;nbsp;Nunes），是澳門望德堂區南部的街道，位於高偉樂街和東望洋街之間，與荷蘭園大馬路和東望洋街平行。呈東北至西南走向。為南北單線行車。本街有不少泰國人經營的商店，故有“小泰國”之稱。又稱荷蘭園二馬路為“泰國街”。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201031/1604111571739192.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;600&quot; height=&quot;401&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 600px; height: 401px;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;泰國文化節&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;以泰國文化、美食為主軸，有&lt;/span&gt;“小曼谷”之稱的荷&lt;/span&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;蘭園二馬路設泰國美食街及多個展銷攤位，配合泰國民族舞、搖滾樂隊、泰拳表演，展現多姿多彩的泰國文化&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em; text-indent: 0em; text-align: center;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201031/1604111531577612.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;800&quot; height=&quot;400&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 800px; height: 400px;&quot;/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p dir=&quot;rtl&quot; style=&quot;line-height: normal; text-indent: 0em; text-align: center;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;br/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2020-10-31/5f9ccd364beb2.jpg', '/Public/upload/2020-10-31/5f9ccd364beb2_width_300.jpg', NULL, 'published', 1, 1604586044, NULL, 5, 1540520690, 1604586044, NULL),
(7, 1, 2, '泰國文化節', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;以泰國文化、美食為主軸，有“小曼谷”之稱的荷蘭園二馬路設泰國美食街及多個展銷攤位，配合泰國民族舞、搖滾樂隊、泰拳表演，展現多姿多彩的泰國文化。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540520759127348.jpg&quot; title=&quot;1540520759127348.jpg&quot; alt=&quot;12295422_1105285259503972_4394352937068144994_n.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540520741181822.jpg&quot; title=&quot;1540520741181822.jpg&quot; alt=&quot;DSC_6137.JPG&quot;/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd27b572966e.JPG', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540520795, 1540520795, 1540530438),
(8, 1, 3, '望德堂故事', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: center; line-height: 3em; text-indent: 0em;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201105/1604586569121379.png&quot; title=&quot;&quot; alt=&quot;望德堂区地圖.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;坐落在澳門著名街道荷蘭園大馬路背後的望德堂區，是澳門中區一處恍如南歐小鎮的舊城區，因第一座華人教堂望德聖母堂巍然矗立而得名。1569年，創立仁慈堂的賈尼路主教，在望德堂區開設拉匝祿痲瘋病院，望德堂因此被稱為“瘋堂”，附近一帶形成的街道也因而“瘋”起來，有瘋堂斜巷、瘋堂新街、瘋堂裡、瘋堂圍、瘋堂中斜巷等。1984年望德堂坊區被列入政府保護文化財產之一。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; line-height: 3em; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;https://www.hollandshow.cn/ueditor/php/upload/image/20201031/1604108588138953.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;1000&quot; height=&quot;432&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; white-space: normal; width: 1000px; height: 432px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201031/1604108637116354.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;600&quot; height=&quot;441&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 600px; height: 441px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; line-height: 3em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;聖約翰節&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;該節日為紀念1622年&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;6&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;月24&lt;/span&gt;日，澳門成功以大炮驅趕了荷蘭兵的入侵。而當日又剛好是聖約翰紀念日，故稱當天為“聖約翰節”。人們每年都舉行多項慶祝活動，包括各項音樂表演、舞蹈，同場還設有澳門傳統遊戲、土生及葡萄牙特色美食、手工藝品及書籍等攤位，極具本土特色，場面非常熱鬧。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;活動時間：&lt;span style=&quot;font-size: 16px; font-family: Calibri;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 32px;&quot;&gt;6&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 32px;&quot;&gt;月24&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 32px;&quot;&gt;日&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;活動地點：望德堂瘋堂斜巷&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; line-height: 3em;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201031/1604109852769632.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;600&quot; height=&quot;432&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 600px; height: 432px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;黃昏小敘&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;逢週六午後走過寧靜的望德堂一帶，您會發現街道兩旁擺放藝術創意攤位，出售本土原創產品；並有創作人即席創作及表演如繪畫、手工藝、音樂演奏等，把聖祿杞街打造成休閒風情的藝文區，拉闊生活趣味，傾聽悠揚的樂聲，與創作人即時交流或三五知己閒聊，享受一個充實的下午。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 32px;&quot;&gt;活動時間：逢週六下午3時至&lt;/span&gt;&lt;span style=&quot;text-indent: 32px; font-family: Calibri;&quot;&gt;6&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 32px;&quot;&gt;時&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;活動地點：澳門望德堂聖祿杞街&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; line-height: 3em;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201031/1604109859967733.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;600&quot; height=&quot;450&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 600px; height: 450px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2020-10-31/5f9cc6435f8f7.jpg', '/Public/upload/2020-10-31/5f9cc6435f8f7_width_300.jpg', NULL, 'published', 0, NULL, NULL, 4, 1540521066, 1604586572, NULL),
(9, 1, 4, '雀仔園故事', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: center; line-height: 3em; text-indent: 0em;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201105/1604586031110481.png&quot; title=&quot;&quot; alt=&quot;雀仔园地图-01.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 2em;&quot;&gt;雀仔園位於澳門半島中部，背靠東望洋山的南段，前臨水坑尾街，房屋依山而建，街道縱橫相交，呈方格狀。雀仔園是早期澳門的邊緣，正位於舊城牆的內邊，附近的樹林茂密，雀鳥甚多，因而得此名。正式命名於1905年&lt;/span&gt;&lt;span style=&quot;text-indent: 2em; font-family: Calibri;&quot;&gt;(&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 2em;&quot;&gt;清光緒三十一年&lt;/span&gt;&lt;span style=&quot;text-indent: 2em; font-family: Calibri;&quot;&gt;)&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 2em;&quot;&gt;。雀仔園縱列的街道是沿山坡修建的斜路，兩端高差達致&lt;/span&gt;&lt;span style=&quot;text-indent: 2em; font-family: Calibri;&quot;&gt;15&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 2em;&quot;&gt;米，從南到北依次為：魯子高街、臘八街、羅憲新街&lt;/span&gt;&lt;span style=&quot;text-indent: 2em; font-family: Calibri;&quot;&gt;(&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 2em;&quot;&gt;澳門石級最多的街道，有&lt;/span&gt;&lt;span style=&quot;text-indent: 2em; font-family: Calibri;&quot;&gt;9&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 2em;&quot;&gt;層&lt;/span&gt;&lt;span style=&quot;text-indent: 2em; font-family: Calibri;&quot;&gt;72&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 2em;&quot;&gt;級&lt;/span&gt;&lt;span style=&quot;text-indent: 2em; font-family: Calibri;&quot;&gt;)&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px; text-indent: 2em;&quot;&gt;、愕街、崗陵街。在百年前，此處已經建成了熱鬧繁華的“雀仔園街市”和香火鼎盛的“福德祠”。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&amp;nbsp;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540526010119233.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;600&quot; height=&quot;397&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 600px; height: 397px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px; font-family: 宋体;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;魯班誕&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;農曆六月十三日，是魯班誕正日。魯班誕，在建築行業內俗稱“師傅誕”，在慶祝的日子，會舉辦傳統的派“師傅飯”活動——派發加了粉絲、蝦米、眉豆等的白飯。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201030/1604050581427343.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;600&quot; height=&quot;408&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 600px; height: 408px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;土地誕&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-size: 18px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;農曆二月初二是土地誕，雀仔園福德祠土地廟舉辦的土地誕最具規模。屆時，會舉辦神功戲和搶花炮活動。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; text-indent: 0em;&quot; dir=&quot;ltr&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540526065955223.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;600&quot; height=&quot;400&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 600px; height: 400px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd29007ada26.jpg', '/Public/upload/2018-10-26/5bd29007ada26_width_300.jpg', NULL, 'published', 1, 1604586032, NULL, 3, 1540524046, 1604586032, NULL),
(10, 1, 4, '魯班誕', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;農曆六月十三日，是魯班誕正日。魯班誕，在建築行業內俗稱“師傅誕”，在慶祝的日子，會舉辦傳統的派“師傅飯”活動——派發加了粉絲、蝦米、眉豆等的白飯。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540524383553626.jpg&quot; title=&quot;1540524383553626.jpg&quot; alt=&quot;1.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd2896f9af74.jpg', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540524403, 1540524403, 1540525817),
(11, 1, 4, '土地誕', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;農曆二月初二是土地誕，雀仔園福德祠土地廟舉辦的土地誕最具規模。屆時，會舉辦神功戲和搶花炮活動。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540524535707321.jpg&quot; title=&quot;1540524535707321.jpg&quot; alt=&quot;1-130304111150.jpg&quot;/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd28a07cfb2c.jpg', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540524560, 1540524560, 1540525825),
(12, 1, 4, '魯班廟', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;魯班廟又稱師傅廟，始建年份不晚于咸豐九年（1859年）。廟門楹聯：“準繩留萬古，德象著千秋。”廟宇為本澳建築行業仁德堂所建，後經多次重修。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;地址：白灰街41號&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, NULL, '', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540524694, 1540524694, 1540525834),
(13, 1, 4, '福德祠土地廟', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;福德祠始建年代已難稽考，估計當在清代中葉以後。廟外牆現存古碑一幀，立於光緒十二年（一八八六），提及“本坊向有土地祠，今因本澳官憲修泊道路，隧將久祠遷建此地。”可見現存福德祠為後來遷建。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;地址：羅憲新街8號&lt;span style=&quot;font-size: 16px; font-family: Calibri;&quot;&gt;A&lt;/span&gt;樓宇旁&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, NULL, '', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540524787, 1540524787, 1540525840),
(14, 1, 4, '羅憲新街', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;羅憲新街是目前全澳門石級最多的街道，有9層&lt;span style=&quot;font-family: Calibri;&quot;&gt;72&lt;/span&gt;級。官方命名於&lt;span style=&quot;font-family: Calibri;&quot;&gt;1887&lt;/span&gt;年&lt;span style=&quot;font-family: Calibri;&quot;&gt;1&lt;/span&gt;月&lt;span style=&quot;font-family: Calibri;&quot;&gt;20&lt;/span&gt;日。街道中的“羅”是指第&lt;span style=&quot;font-family: Calibri;&quot;&gt;92&lt;/span&gt;任澳門總督羅沙（&lt;span style=&quot;font-family: Calibri;&quot;&gt;Tomás de Sousa Rosa&lt;/span&gt;），“憲”則是源於明清時中國人用作對上司的尊重。“羅憲”即是指“羅沙總督大人”。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, NULL, '', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540524875, 1540524875, 1540525891),
(15, 1, 4, '雀仔園街市', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;澳門九大街市之一的雀仔園街市，重建於1939年，距今已經逾五十年了。區內原有舊墟市，由於區內的居民日益增加，百年前建造的木質結構日漸頹破，&lt;span style=&quot;font-family: Calibri;&quot;&gt;1939&lt;/span&gt;年遭澳葡當局拆除殘宇，重新改建為如今的新式街市。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540524919120320.jpg&quot; title=&quot;1540524919120320.jpg&quot; alt=&quot;Fig 8.jpg&quot;/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd28b8181796.jpg', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540524932, 1540524932, 1540525896),
(16, 1, 3, '聖約翰節', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;該節日為紀念1622年&lt;span style=&quot;font-family: Calibri;&quot;&gt;6&lt;/span&gt;月&lt;span style=&quot;font-family: Calibri;&quot;&gt;24&lt;/span&gt;日，澳門成功以大炮驅趕了荷蘭兵的入侵。而當日又剛好是聖約翰紀念日，故稱當天為“聖約翰節”。人們每年都舉行多項慶祝活動，包括各項音樂表演、舞蹈，同場還設有澳門傳統遊戲、土生及葡萄牙特色美食、手工藝品及書籍等攤位，極具本土特色，場面非常熱鬧。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;*活動時間：&lt;span style=&quot;font-size: 16px; font-family: Calibri;&quot;&gt;6&lt;/span&gt;月&lt;span style=&quot;font-size: 16px; font-family: Calibri;&quot;&gt;24&lt;/span&gt;日&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;*活動地點：望德堂瘋堂斜巷&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540525244660647.jpg&quot; title=&quot;1540525244660647.jpg&quot; alt=&quot;2016-06-25_12-13-29.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540525232210446.jpg&quot; title=&quot;1540525232210446.jpg&quot; alt=&quot;tuna.jpg&quot;/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd28cd67f601.jpg', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540525277, 1540525277, 1540529793),
(17, 1, 3, '黃昏小敘', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;逢週六午後走過寧靜的望德堂一帶，您會發現街道兩旁擺放藝術創意攤位，出售本土原創產品；並有創作人即席創作及表演如繪畫、手工藝、音樂演奏等，把聖祿杞街打造成休閒風情的藝文區，拉闊生活趣味，傾聽悠揚的樂聲，與創作人即時交流或三五知己閒聊，享受一個充實的下午。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;地點：澳門望德堂聖祿杞街&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;時間：逢週六下午3時至&lt;span style=&quot;font-size: 16px; font-family: Calibri;&quot;&gt;6&lt;/span&gt;時&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540525358104353.jpg&quot; title=&quot;1540525358104353.jpg&quot; alt=&quot;17966352_955414634561504_474684557155474107_o.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd28d3f5ddf9.jpg', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540525384, 1540525384, 1540529799),
(18, 1, 3, '瘋堂巡禮', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;由望德堂區創意產業促進會聯同區內多個文創單位合作舉辦的每年一次的“瘋堂巡禮”活動於十一月舉行。屆時，視覺藝術、電影、陶藝、攝影、音樂、時裝、話劇、動漫玩樂及文創展覽等活動應有盡有。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;*活動時間：十一月&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体; font-size: 16px;&quot;&gt;*活動舉辦地點：望德堂區&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 0em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 0em; text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540525516116991.jpg&quot; title=&quot;1540525516116991.jpg&quot; alt=&quot;20151117095104645.jpg&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 0em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 0em; text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181026/1540525504880490.jpg&quot; title=&quot;1540525504880490.jpg&quot; alt=&quot;12246717_970033326403299_4739042590143886776_n.jpg&quot;/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-26/5bd28dd6ccd39.jpg', NULL, NULL, 'published', 0, NULL, NULL, 0, 1540525531, 1540525531, 1540529796),
(19, 1, 6, '羅憲新街', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;text-align: left; text-indent: 2em;&quot;&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: 宋体; text-align: justify; text-indent: 26px; background-color: rgb(255, 255, 255);&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em; text-indent: 0em; text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201105/1604585986454191.png&quot; title=&quot;&quot; alt=&quot;罗宪新街地图-01.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;地處愕街的西南面。西北端在水坑尾街正對鴨巷，向東南延伸與柯高街、馬大臣街、公局市場巷、高冠街等相交至東望洋新街為止。全長約147米，闊約6米。官方命名於1887年1月20日。該街由柯高街至東望洋新街的一段屬於雀仔園範圍。羅憲新街是目前全澳門最多石階級的街道，共分為九截，每截各有八級石階，合共72級。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;街道名稱中的&amp;quot;羅&amp;quot;字是指第92任澳門總督羅沙(Tomás de Sousa Rosa)，他於1883年4月23日上任澳督，至1886年卸任。&amp;quot;憲&amp;quot;字則源於明清時中國人用作對上司的尊重。&amp;quot;羅憲&amp;quot;其實就是意指&amp;quot;羅沙總督大人&amp;quot;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: left;&quot;&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: 宋体; text-align: justify; text-indent: 26px; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: 宋体; text-align: justify; text-indent: 26px; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181029/1540777197119986.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;600&quot; height=&quot;392&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 600px; height: 392px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-29/5bd6651cb7d7a.jpg', '/Public/upload/2018-10-29/5bd6651cb7d7a_width_300.jpg', NULL, 'published', 0, NULL, NULL, 2, 1540777312, 1604585989, NULL),
(20, 1, 5, '東望洋街', NULL, NULL, '', NULL, NULL, '&lt;p style=&quot;line-height: 3em; text-indent: 0em; text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201105/1604586021584475.png&quot; title=&quot;&quot; alt=&quot;东望洋街地图-01.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;東望洋街，東起高偉樂街和士多鳥拜斯大馬路，西至荷蘭園大馬路、水坑尾街和水井斜巷。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;東望洋街築成於&lt;/span&gt;1847&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;至&lt;/span&gt;1848&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;年之間，先有葡文名稱，後有中文街名，但中葡文街名的意思完全不相干。葡文街名取自亞馬留總督的姓名，正式公佈於&lt;/span&gt;1869&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;年&lt;/span&gt;7&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;月&lt;/span&gt;16&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;日的澳葡政府憲報，以紀念阿馬留總督築路事蹟。中文街名較早見載於&lt;/span&gt;1905&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;年的《澳門海外省街道名冊》，因該街位於東望洋山山麓，故以此山命名。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 3em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;阿馬留總督在修築馬路過程中，曾激發民憤，最終被村民沈米等人刺殺。東望洋街，位於北城牆水坑尾閘口之外，城牆外為蕉園，內為雀仔園。東望洋街築成後，加快了周邊地區的城市化發展。水坑尾閘門、炮臺、碉堡和城牆漸次被拆毀，蕉園和雀仔園連成一片，園中修築道路，興建房屋和市場，成為繁榮的街區。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 3em; text-indent: 0em; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20201031/1604115823877359.jpg&quot; title=&quot;&quot; alt=&quot;&quot; width=&quot;600&quot; height=&quot;398&quot; border=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 600px; height: 398px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, NULL, '/Public/upload/2018-10-29/5bd671d018d8b.jpg', '/Public/upload/2018-10-29/5bd671d018d8b_width_300.jpg', NULL, 'published', 1, 1629191369, NULL, 1, 1540780511, 1629191369, NULL),
